import * as vscode from 'vscode';
import fetch from 'node-fetch';

export function activate(context: vscode.ExtensionContext) {
    const disposable = vscode.commands.registerCommand('my-copilot.explainCode', async () => {
        const editor = vscode.window.activeTextEditor;
        if (!editor) return;

        const selection = editor.document.getText(editor.selection);
        if (!selection) {
            vscode.window.showInformationMessage('Please select some code first.');
            return;
        }

        const explanation = await getExplanationFromOpenAI(selection);
        vscode.window.showInformationMessage(explanation);
    });

    context.subscriptions.push(disposable);
}

async function getExplanationFromOpenAI(code: string): Promise<string> {
    const OPENAI_API_KEY = 'YOUR_OPENAI_API_KEY'; // replace with your key

    const res = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
            'Authorization': `Bearer ${OPENAI_API_KEY}`,
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            model: 'gpt-4o',
            messages: [
                { role: 'system', content: 'You are a helpful programming assistant.' },
                { role: 'user', content: `Explain the following code:\n\n${code}` }
            ],
            temperature: 0.7
        })
    });

    const data = await res.json();
    return data.choices?.[0]?.message?.content ?? 'No explanation found.';
}

export function deactivate() {}
```

