(async function () {
  const videoId = new URLSearchParams(window.location.search).get("v");
  const transcriptBox = document.getElementById("transcript");

  if (!videoId) {
    transcriptBox.innerText = "Invalid YouTube URL.";
    return;
  }

  const response = await fetch(`https://www.youtube.com/watch?v=${videoId}`);
  const html = await response.text();

  const captionsRegex = /"captions":({.*?isTranslatable":(true|false)}])/;
  const match = captionsRegex.exec(html);

  if (!match) {
    transcriptBox.innerText = "Transcript not available.";
    return;
  }

  const captions = JSON.parse(match[1] + "]");
  const captionTracks = captions.playerCaptionsTracklistRenderer.captionTracks;

  if (!captionTracks || captionTracks.length === 0) {
    transcriptBox.innerText = "No captions found.";
    return;
  }

  const transcriptUrl = captionTracks[0].baseUrl;

  const transcriptRes = await fetch(transcriptUrl);
  const transcriptXml = await transcriptRes.text();

  const parser = new DOMParser();
  const xmlDoc = parser.parseFromString(transcriptXml, "text/xml");

  const texts = xmlDoc.getElementsByTagName("text");
  let fullTranscript = "";
  for (let i = 0; i < texts.length; i++) {
    const line = texts[i].textContent.replace(/&#39;/g, "'");
    fullTranscript += line + "\n";
  }

  transcriptBox.innerText = fullTranscript || "Transcript loaded, but empty.";
})();
```

