void minHeapify() {
    if(size() <= 1)
        return;

    for(int i = (size()-2)/2; i >= 0; i--) {
        int k = i;
        while(2*k+1 < size()) {
            int j = 2*k + 1;
            task_struct* left = get_task(j);
            float ratio1 = static_cast<float>(left->priority) / left->remaining_cycles;
            float ratio2 = 0.0;

            if(j+1 < size()) {
