            float finalRatio2 = static_cast<float>(get_task(k)->priority) / get_task(k)->remaining_cycles;

            if(finalRatio1 < finalRatio2 || (finalRatio1 == finalRatio2 && get_task(j)->priority < get_task(k)->priority)) {
                swap_tasks(get_task(j), get_task(k));
                k = j;
